const getCookieValues = require('getCookieValues');

const measurementId = data.measurementId;
const prefix = data.prefix;

var cookieName = '_ga_' + measurementId.slice(2);
var cookie = getCookieValues(cookieName);

if (!cookie || !cookie.length || typeof cookie[0] !== 'string') return undefined;
var prefixIndex = cookie[0].indexOf(prefix);

if (prefixIndex === -1) return undefined;
var substringAfterPrefix = cookie[0].substring(prefixIndex + 1);
var dollarIndex = substringAfterPrefix.indexOf('$');

if (dollarIndex !== -1) {
    return substringAfterPrefix.substring(0, dollarIndex);
  } else {
    // If no '$' is found, the value goes to the end of the string
    return substringAfterPrefix;
  }


